mapper.select:

      select 
          QA_NUM, MBSP_ID, QA_REGDATE, QA_CONTENT, MBSP_ID
      from 
          (
          select /*+INDEX_DESC(QA_TBL PK_QA_NUM) */	
              rownum rn, qa_num, mbsp_id, qa_regdate, qa_content, mbsp_id
          from 
              QA_TBL
          where 

             qa_num = #{qa_num}
             and    
              rownum <= #{cri.pageNum} * #{cri.amount}
          )
      where 
          rn > (#{cri.pageNum} -1) * #{cri.amount}



		rownum <= #{pageNum} * #{amount}
	)
	where rn > (#{pageNum} -1) * #{amount}
