mapper.insert:

BEGIN
    UPDATE qa_tbl 
    SET step = step + 1
    WHERE root = #{root} AND step > #{step};

    INSERT INTO QA_TBL (qa_num, root, qa_writer, qa_content, qa_title, step, indent)
    VALUES (seq_qa_num.NEXTVAL, #{root}, #{qa_writer}, #{qa_content}, #{qa_title}, #{step} + 1, #{indent} + 1);
END;
